export function addClass(element,name){if(!element)return;if(typeof name==="string"){if(element.classList){element.classList.add(name)}else{let arr=element.className.split(" ");if(arr.indexOf(name)===-1)element.className+=" "+name}}else{name.forEach(className=>{addClass(element,className)})}return element}export function toggleClass(element,className){if(element.classList){element.classList.toggle(className)}else{let classes=element.className.split(" ");let i=classes.indexOf(className);if(i>=0){classes.splice(i,1)}else{classes.push(className)}element.className=classes.join(" ")}return element}export function hasClass(element,name){if(!element)return;if(element.classList){return element.classList.contains(name)}else{let classes=element.className.split(" ");let i=classes.indexOf(name);return i>=0}}export function removeClass(element,name){if(!element)return;if(element.classList){element.classList.remove(name)}else{let classes=element.className.split(" ");let i=classes.indexOf(name);if(i>=0)classes.splice(i,1);element.className=classes.join(" ")}return element}export function isInput(name){let compare=name.toLowerCase();return["input","textarea","select"].includes(compare)}export function vibrate(pattern){return window.navigator.vibrate(pattern)}export function initThemeSwitcher(element){if(!element)return;const firstSpan=element.getElementsByTagName("span")[0];const icon=firstSpan.getElementsByTagName("i")[0];const isDark=hasClass(document.documentElement,"dark");removeClass(icon,isDark?"fa-moon":"fa-sun");addClass(icon,isDark?"fa-sun":"fa-moon");removeClass(element,isDark?"is-light":"is-dark");addClass(element,isDark?"is-dark":"is-light");removeClass(element,"is-invisible");return element}export function setTheme(){if(localStorage.halivertsTheme&&localStorage.halivertsTheme==="light"){removeClass(document.documentElement,"dark");localStorage.halivertsTheme="light"}else{localStorage.removeItem("halivertsTheme")}return localStorage.halivertsTheme||"dark"}export function setActive(menu){if(!menu)return;const elements=menu.querySelectorAll("li > a");removeClass(menu.querySelector("li > .is-active"),"is-active");for(let i=0;i<elements.length;i++){let element=elements[i];if(window.location.href===element.href){addClass(element,"is-active");return element}}}export function hideModal(target){if(!target)return;const modal=target.closest(".modal");if(!modal)return;addClass(modal,"is-hidden");setActive(document.getElementById("side-menu"));return modal}window.vibrate=vibrate;window.hideModal=hideModal;