import{initThemeSwitcher,setTheme,hideModal,setActive}from"./functions.js";setTheme();document.addEventListener("turbo:load",()=>{initThemeSwitcher(document.getElementById("theme-switcher"));initThemeSwitcher(document.getElementById("theme-switcher-corner"));const deletes=document.querySelectorAll(".notification .delete")||[];deletes.forEach(deleteElement=>{let notification=deleteElement.parentElement;deleteElement.addEventListener("click",()=>{notification.parentNode.removeChild(notification)})});const modals=document.querySelectorAll(".modal")||[];modals.forEach(modal=>{modal.addEventListener("click",evt=>{if(evt.target!==modal){return}evt.preventDefault();hideModal(evt.target)})});setActive(document.getElementById("side-menu"))});document.addEventListener("turbo:visit",()=>{setTheme()});